class Solution { public: int lengthOfLongestSubstring(string s) { unordered_map<char, int> hash; int left = 0, right = 0, maxLen = 0; while (right < s.length()) { char c = s[right]; if (hash.find(c) != hash.end() && hash[c] >= left) { left = hash[c] + 1; } hash[c] = right; maxLen = max(maxLen, right - left + 1); right++; } return maxLen; } };
